startPos = (0, myMap[0].index('|'))

#       0
#   3       1
#       2
direction = DOWN
xPos = startPos[1]
yPos = startPos[0]

print(startPos)

finalString = ""
steps = 0

while True:
    #print(direction)
    steps += 1
    if direction == DOWN:
        yPos += 1
    elif direction == UP:
        yPos -= 1
    elif direction == RIGHT:
        xPos += 1
    elif direction == LEFT:
        xPos -= 1
    else:
        break

    if myMap[yPos][xPos] == '|':
        pass
    elif myMap[yPos][xPos] == '-':
        pass
    elif myMap[yPos][xPos] == '+':
        if direction == DOWN or direction == UP:
            try:
                if myMap[yPos][xPos+1] != ' ':
                    direction = RIGHT
            except:
                pass
            try:
                if myMap[yPos][xPos-1] != ' ':
                    direction = LEFT
            except:
                pass
        elif direction == RIGHT or direction == LEFT:
            try:
                if myMap[yPos-1][xPos] != ' ':
                    direction = UP
            except:
                pass
            try:
                if myMap[yPos+1][xPos] != ' ':
                    direction = DOWN
            except:
                pass
    else:
        finalString += myMap[yPos][xPos]
        print(finalString, 'myMap', myMap[yPos][xPos], 'steps', steps)

